"use strict";

var _ = require("lodash");

module.exports = unparse;

function unparse(slices) {
  var stringOfSlices = _(slices)
    .map(function(slice) {
      return [slice.top, slice.left, slice.bottom, slice.right].join(" ");
    })
    .join("\n");
  return slices.length + "\n" + stringOfSlices;
}
