"use strict";

var _ = require("lodash");

module.exports = unparse;

function unparse(moves) {
  return _(moves)
    .map(function(movesForStep) {
      validate(movesForStep);
      return movesForStep.join(" ");
    })
    .join("\n");
}

function validate(movesForStep) {
  _.each(movesForStep, function(move) {
    if (move !== -1 && move !== 0 && move !== 1) throw new Error("invalid move: " + move + " (" + (typeof move) + ")");
  });
}
